/*****  Markdown ‚Üí Telegram Markdown‚ÄØV2  *****
 *  ‚ìÖ 2025 ‚Äì drop‚Äëin per n8n (zero dipendenze)
 ************************************************/

// ‚Äî¬†costanti pre‚Äëcompilate (una volta sola) ‚Äî
const RE_CODE  = /```[\s\S]*?```|`[^`]*?`/g;                     // blocchi + inline
const RE_BOLD  = /\*\*([\s\S]+?)\*\*|__([\s\S]+?)__/g;           // **bold** / __bold__
const RE_ITAL  = /(?<!\*)\*([^\n*]+?)\*(?!\*)|(?<!_)_([^\n_]+?)_(?!_)/g; // *italic* / _italic_
const RE_STRK  = /~([^~]+?)~/g;                                  // ~strike~
const RE_RSRV  = /[_*[\]()~`>#+\-=|{}.!\\]/g;                    // tutti i ‚Äúreserved‚Äù
const PLACE    = c => `\u0000${c}\u0000`;                        // placeholder NUL

// escape ‚Äúreserved‚Äù in un testo normale
const esc = s => s.replace(RE_RSRV, '\\$&');

// sostituisce segmenti con placeholder, salvandoli in bag[]
function stashReplace(txt, re, bag, replacer) {
  return txt.replace(re, (...m) => {
    const seg = replacer(...m);           // testo gi√† formattato + escaped
    bag.push(seg);
    return PLACE(bag.length - 1);
  });
}

function mdToTgV2(src) {
  let out = src;
  const bag = [];

  /* 1) congelo codice: NON va mai escappato n√© toccato */
  out = stashReplace(out, RE_CODE, bag, m => m);

  /* 2) grassetto ‚Üí *‚Ä¶* + escape interno, poi congelato */
  out = stashReplace(out, RE_BOLD, bag,
    (_, g1, g2) => `*${esc((g1 ?? g2).trim())}*`
  );

  /* 3) corsivo ‚Üí _‚Ä¶_ + escape interno, poi congelato */
  out = stashReplace(out, RE_ITAL, bag,
    (_, g1, g2) => `_${esc((g1 ?? g2).trim())}_`
  );

  /* 4) barrato ‚Üí ~‚Ä¶~  (Telegram accetta ~ come marcatore) */
  out = stashReplace(out, RE_STRK, bag,
    (_, g) => `~${esc(g.trim())}~`
  );

  /* 5) escape globale dei reserved rimanenti  */
  out = esc(out)
        .replace(/(^|\n)-/g, '$1\\-');            // bullet list ‚Äú\-‚Äù

  /* 6) ripristino di tutti i placeholder */
  return out.replace(/\u0000(\d+)\u0000/g, (_, i) => bag[i]);
}

/* ‚Äî‚Äî‚Äî n8n wrapper ‚Äî‚Äî‚Äî */
return $input.all().map(it => {
  it.json.message = mdToTgV2(it.json.message || '');
  return it;
});

const chatHistory = $input.first().json.get_user_activity.user_messages;

let xmlString = "<chat_history>\n";

for (const message of chatHistory) {
  xmlString += ` <message id="${message.message_id}" channel="${message.channel_id}" timestamp="${message.created_at}">\n`;
  xmlString += ` <content><![CDATA[${message.content}]]></content>\n`;
  xmlString += ` </message>\n`;
}

xmlString += "</chat_history>";

return { chat_history: xmlString };
